    static int countGroups(List<String> related) {

        if (related == null) return 0;
        if (related.isEmpty()) return 0;


        int[][] matrix = new int[related.size()][related.get(0).length()];

        // Construct matrix
        for (int i = 0; i < related.size(); i++) {
            for (int j = 0; j < related.get(i).toCharArray().length; j++) {
                if (related.get(i).charAt(j) == '1') matrix[i][j] = 1;
                else matrix[i][j] = 0;
            }
        }

        // Traverse to find connected components
        int groups = 0;
        for (int i = 0; i < matrix.length; i++) {
            for (int j = 0; j < matrix[0].length; j++) {
                if (matrix[i][j] == 1) {
                    dfs(matrix, i, j);
                    groups++;
                }
            }
        }

        return groups;
    }

    static void dfs(int[][] matrix, int i, int j) {
        if (inBounds(matrix, i, j) && matrix[i][j] == 1) {
            matrix[i][j] = 0;
            dfs(matrix, i, j + 1);
            dfs(matrix, i + 1, j);
            dfs(matrix, i, j - 1);
            dfs(matrix, i - 1, j);
        }
    }

    private static boolean inBounds(int[][] matrix, int i, int j) {
        return i >= 0 && i < matrix.length && j >= 0 && j < matrix.length;
    }

    static long howManySwaps(List<Integer> arr) {
        ArrayList<Integer> nums = new ArrayList<>(arr);
        int swaps = 0;

        int i = 0;
        boolean found = false;
        while (i < nums.size()) {
            int j = i + 1;
            while (j < nums.size() && !found) {
                if (nums.get(i) > nums.get(j)) {
                    Collections.swap(nums, i, j);
                    i = 0;
                    swaps++;
                    found = true;
                } else {
                    j++;
                }
            }
            if (!found) {
                i++;
            } else {
                found = false;
            }

        }
        return swaps++;
    }

